
class Ztimer{constructor(t){null==t.starttime?this.starttime="0,0,0":this.starttime=t.starttime,this.endtime=t.endtime,this.selector=t.selector,this.currentTime=0,this.endTime,this.h=0,this.min=0}RealClockTime(t){let e=this.currentTime%60;this.currentTime%60==0?this.min=this.currentTime/60:this.min=Math.floor(this.currentTime/60),this.min%60==0?this.h=this.min/60:this.h=Math.floor(this.min/60);let i=`${this.h}:${this.min}:${e}`;document.querySelector(this.selector).innerText=i}StartTimer(){this.CalibrateClock();let t=setInterval(()=>{this.currentTime++,this.currentTime==this.endTime&&clearInterval(t),this.RealClockTime(this.currentTime)},1e3)}CalibrateClock(){let t=this.starttime.split(","),e=this.endtime.split(",");this.currentTime=60*parseInt(t[0])+60*parseInt(t[1])+parseInt(t[2]),this.endTime=60*parseInt(e[0])+60*parseInt(e[1])+parseInt(e[2])}}
