class Ztimer{constructor(t){null==t.starttime?this.starttime="0,0,0":this.starttime=t.starttime,this.endtime=t.endtime,this.selector=t.selector,this.currentTime=0,this.endTime,this.h=0,this.min=0}RealClockTime(){let t=this.currentTime%60;this.min=Math.floor(this.currentTime/60),this.h=Math.floor(this.currentTime/3600);let e=`${this.h}:${this.min}:${t}`;document.querySelector(this.selector).innerText=e}StartTimer(){this.CalibrateClock();let t=setInterval(()=>{this.currentTime++,this.currentTime==this.endTime&&clearInterval(t),this.RealClockTime()},1e3)}CalibrateClock(){let t=this.starttime.split(","),e=this.endtime.split(",");this.currentTime=60*parseInt(t[0])+60*parseInt(t[1])+parseInt(t[2]),this.endTime=60*parseInt(e[0])+60*parseInt(e[1])+parseInt(e[2]),console.log(this.currentTime),console.log(this.endTime)}}